#!/bin/bash -eux

# Script is necessary when using AWS builder. Creates vagrant user. Virtualbox and VMWare use a kickstart file to do this.

sudo useradd -m -s /bin/bash -G wheel vagrant
echo "vagrant" | sudo  passwd --stdin vagrant

mkdir /home/vagrant/.ssh
echo "ssh-rsa  vagrant insecure public key" >> /home/vagrant/.ssh/authorized_keys
